const steps = document.querySelectorAll(".step");
  const nextBtns = document.querySelectorAll(".btn-next");
  const prevBtns = document.querySelectorAll(".btn-prev");
  const progressbar = document.querySelectorAll(".progressbar li");
  const slots = document.querySelectorAll(".slot");

  let currentStep = 0;

  // References for receipt fields
  const station = document.getElementById("station");
  const date = document.querySelector("input[type='date']");
  const vehicleType = document.getElementById("vehicle-type");
  const vehicleNumber = document.getElementById("vehicle-number");
  const nameField = document.getElementById("name");
  const phone = document.getElementById("phone");
  const email = document.getElementById("email");

  const rStation = document.getElementById("r-station");
  const rDate = document.getElementById("r-date");
  const rSlot = document.getElementById("r-slot");
  const rVehicleType = document.getElementById("r-vehicle-type");
  const rVehicleNumber = document.getElementById("r-vehicle-number");
  const rName = document.getElementById("r-name");
  const rPhone = document.getElementById("r-phone");
  const rEmail = document.getElementById("r-email");

  // âœ… Validation function
  function validateStep(stepIndex) {
    let valid = true;
    const inputs = steps[stepIndex].querySelectorAll("input, select");

    inputs.forEach(input => {
      if (input.hasAttribute("required") && !input.value.trim()) {
        input.style.border = "2px solid red";
        valid = false;
      } else {
        input.style.border = "none";
      }
    });

    if (stepIndex === 0) {
      const activeSlot = steps[stepIndex].querySelector(".slot.active");
      if (!activeSlot) {
        alert("Please select a time slot");
        valid = false;
      }
    }

    return valid;
  }

  // âœ… Update receipt values
  function updateReceipt() {
    const activeSlot = document.querySelector(".slot.active");

    rStation.textContent = station.value;
    rDate.textContent = date.value;
    rSlot.textContent = activeSlot ? activeSlot.textContent : "Not selected";
    rVehicleType.textContent = vehicleType.value;
    rVehicleNumber.textContent = vehicleNumber.value;
    rName.textContent = nameField.value;
    rPhone.textContent = phone.value;
    rEmail.textContent = email.value;
  }

  // âœ… Next button
  nextBtns.forEach(btn => {
    btn.addEventListener("click", () => {
      if (!validateStep(currentStep)) return;

      if (currentStep < steps.length - 1) {
        steps[currentStep].classList.remove("active");
        progressbar[currentStep].classList.remove("active");
        progressbar[currentStep].classList.add("completed");

        currentStep++;
        steps[currentStep].classList.add("active");
        progressbar[currentStep].classList.add("active");

        // ðŸ”¥ Update receipt when reaching confirmation step
        if (currentStep === steps.length - 1) {
          updateReceipt();
        }
      }
    });
  });

  // âœ… Previous button
  prevBtns.forEach(btn => {
    btn.addEventListener("click", () => {
      if (currentStep > 0) {
        steps[currentStep].classList.remove("active");
        progressbar[currentStep].classList.remove("active");

        currentStep--;
        steps[currentStep].classList.add("active");
        progressbar[currentStep].classList.remove("completed");
        progressbar[currentStep].classList.add("active");
      }
    });
  });

  // âœ… Slot selection
  slots.forEach(slot => {
    slot.addEventListener("click", () => {
      slots.forEach(s => s.classList.remove("active"));
      slot.classList.add("active");
    });
  });

  document.getElementById("download").addEventListener("click", function () {
    const { jsPDF } = window.jspdf;
    const doc = new jsPDF();

    // Random service charge between 100â€“500
    // const amount = (Math.floor(Math.random() * 400) + 100).toFixed(2);
    const amount = 35;
    const logo = "data:image/png;base64,"
    doc.addImage(logo, "PNG", 20, 0, 25, 25);
    // ===== Receipt Layout =====
    doc.setFont("helvetica", "bold");
    doc.setFontSize(22);
    doc.text("BookMyCNG", 60, 20);

    doc.setFont("helvetica", "normal");
    doc.setFontSize(12);
    doc.text("GreenFuel Charging Station", 20, 35);
    doc.text("123 EV Street, Smart City", 20, 42);
    doc.text("(+91) 98765-43210", 20, 49);

    // Line
    doc.line(20, 55, 190, 55);

    // Booking details
    let y = 65;
    doc.setFont("helvetica", "bold");
    doc.text("Booking Details:", 20, y); y += 10;

    // Row 1
    doc.text(`Station: ${rStation.textContent}`, 20, y);
    doc.text(`Date: ${rDate.textContent}`, 120, y);
    y += 8;

    // Row 2
    doc.text(`Time Slot: ${rSlot.textContent}`, 20, y);
    doc.text(`Vehicle: ${rVehicleType.textContent} (${rVehicleNumber.textContent})`, 120, y);
    y += 8;

    // Row 3
    doc.text(`Name: ${nameField.value}`, 20, y);
    doc.text(`Phone: ${rPhone.textContent}`, 120, y);
    y += 8;

    // Row 4 (single column because email can be long)
    doc.text(`Email: ${rEmail.textContent}`, 20, y);
    y += 12;

    // Line
    doc.line(20, y, 190, y); y += 10;

    // Charges
    doc.setFont("helvetica", "bold");
    doc.text("SERVICE CHARGES", 20, y); 
    doc.setFont("helvetica", "normal");
    doc.setFontSize(10);
    doc.text("(Slot Booking Charges)", 20, y + 5);
    doc.text(`â‚¹ ${amount}`, 160, y, { align: "right" }); 
    y += 10;

    doc.setFont("helvetica", "normal");
    doc.text("GST (18%)", 20, y);
    const gst = (amount * 0.18).toFixed(2);
    doc.text(`â‚¹ ${gst}`, 160, y, { align: "right" }); 
    y += 10;

    doc.line(20, y, 190, y); y += 10;

    // Total
    doc.setFont("helvetica", "bold");
    doc.setFontSize(14);
    doc.text("TOTAL", 20, y);
    const total = (parseFloat(amount) + parseFloat(gst)).toFixed(2);
    doc.text(`â‚¹ ${total}`, 160, y, { align: "right" }); 
    y += 15;

    // Footer
    doc.setFontSize(12);
    doc.setFont("helvetica", "italic");
    doc.text("*** THANK YOU FOR BOOKING WITH US ***", 40, y); 
    y += 20;

    // Download
    doc.save("CNG_Booking_Receipt.pdf");
  });

